import { Component, Input } from '@angular/core';
import { Product , products} from '../products';
@Component({
  selector: 'app-product',
  templateUrl: './product.component.html',
  styleUrls: ['./product.component.css']
})
export class ProductComponent {
showBasicDialog() {
throw new Error('Method not implemented.');
}

  products = products;
  @Input() product!:Product;

  share() {
    window.alert('The product has been shared!');
  }

  onNotify() {
    window.alert('You will be notified when the product goes on sale');
  }


  display: boolean = false;
	showDialog() {
        this.display = true;
      }
}
    <h3>
      <a [title]="product.name + ' details'">
        {{ product.name }}
      </a>
      <p-dialog header="{{product.name}}" [(visible)]="display">
        Precio: {{product.price}}
        <img src="https://m.media-amazon.com/images/I/71Nv1150yrL._AC_SX569_.jpg" width="100px" height="100px" alt="Sin imagen">  
      </p-dialog>
      <button
        type="button"
        (click)="showDialog()"
        icon="pi pi-info-circle"
        label="Mostrar"
      ></button>
    </h3>
    <p *ngIf="product.description">
      Description: {{ product.description }}
    </p>

    <button type="button" (click)="share()">
      Share
    </button>

    <app-product-alerts
    [product]="product" 
    (notify)="onNotify()">
    </app-product-alerts>

    <p-dialog header="Title" [(visible)]="display">
      Content
  </p-dialog>

  <button type="button" (click)="showDialog()" icon="pi pi-info-circle" label="Show"></button>
